import { call, put, takeEvery, cancel, select, take, actionChannel, fork, cancelled } from "redux-saga/effects"
import { socketPiping } from "../slices/socketSlice"
import io from "socket.io-client"
import type { Socket } from "socket.io-client"
import { RootState } from "@/store"
import { eventChannel } from "redux-saga"
import type { EventChannel } from "redux-saga"
import { ChatObj } from "@/types"

let socket: Socket | null = null

export const socketDoing = {
  connect: () => {
    socket = io("http://localhost:3000") // Adjust the URL as needed
    return new Promise<Socket>((resolve, reject) => {
      socket!.on("connect", () => {
        resolve(socket!)
      })
      socket!.on("connect_error", (err) => {
        reject(err)
      })
    })
  },
  disconnect: (socket: Socket) => {
    return new Promise<void>((resolve) => {
      socket.disconnect()
      resolve()
    })
  },
}

export function* connect(): Generator<any, void, Socket> {
  console.log("connect socket")
  if (socket && socket.connected) {
    console.log("Socket is already connected")
    return
  }

  try {
    socket = yield call(socketDoing.connect)
    yield put(socketPiping.connected())

    // Fork a new saga to handle incoming messages
    yield fork(handleSocketMessages, socket)
  } catch (error) {
    console.error("Socket connection error:", error)
    alert("Socket connection error:")
    yield put(socketPiping.connectionFailed())
  }
}

function* disconnect(): Generator<any, void, Socket> {
  console.log("disconnect socket")
  if (!socket) {
    console.log("No socket to disconnect")
    return
  }

  yield call(socketDoing.disconnect, socket)
  yield put(socketPiping.disconnected())
}

function createSocketChannel(socket: Socket): EventChannel<any> {
  return eventChannel((emit) => {
    const messageHandler = (chatObj: ChatObj) => {
      emit(chatObj)
    }
    socket.on("message_to_client", messageHandler)
    return () => {
      socket.off("message_to_client", messageHandler)
    }
  })
}

export function* handleSocketMessages(socket: Socket): Generator<any, void, any> {
  console.log("handleSocketMessages")
  const socketChannel = yield call(createSocketChannel, socket)

  try {
    while (true) {
      const chatObj: ChatObj = yield take(socketChannel)
      yield put(socketPiping.receiveMessage(chatObj))
    }
  } finally {
    if (yield cancelled()) {
      socketChannel.close()
    }
  }
}

function* watchSocketRequests(): Generator<any, void, any> {
  const requestChan = yield actionChannel([
    "SOCKET_CONNECT_REQUESTED",
    "SOCKET_DISCONNECT_REQUESTED",
    "SEND_MESSAGE_REQUESTED",
  ])

  let socketTask

  while (true) {
    const action = yield take(requestChan)

    if (action.type === "SOCKET_CONNECT_REQUESTED") {
      if (socketTask) {
        console.log("Socket is already connected. Ignoring connect request.")
      } else {
        socketTask = yield fork(connect)
      }
    } else if (action.type === "SOCKET_DISCONNECT_REQUESTED") {
      if (socketTask) {
        yield cancel(socketTask)
        socketTask = null
        yield call(disconnect)
      } else {
        console.log("Socket is not connected. Ignoring disconnect request.")
      }
    } else if (action.type === "SEND_MESSAGE_REQUESTED") {
      // should sent do backend instead of put to store
      console.log("send message")
      const chatObj = { msg: action.payload }
      socket?.emit("message_from_client", chatObj)
      yield put(socketPiping.receiveMessage(chatObj))
    }
  }
}

export default function* rootSaga() {
  yield fork(watchSocketRequests)
}

import type { Todo, TypedResponse } from "@/types"
import { getTodos, createTodo, updateTodo, deleteTodo } from "@/apiActions"
import { call, put, takeEvery, takeLatest } from "redux-saga/effects"
import { todosPiping } from "../slices/todosSlice"

function* getTodosAction() {
  yield put(todosPiping.startFetchingTodos())
  const { data: todos } = yield call(getTodos)
  yield put(todosPiping.todosFetchSucceeded(todos as Todo[]))
}

function* createTodoAction({ payload }: { type: "CREATE_TODO_REQUESTED"; payload: string }) {
  yield createTodo(payload)
  yield put({ type: "TODOS_FETCH_REQUESTED" })
}

// spliting procedure
function* updateTodoAction({ payload }: { type: "UPDATE_TODO_REQUESTED"; payload: { target: Todo; og: Todo } }) {
  const { target, og } = payload
  try {
    yield put(todosPiping.optimalUpdateTodo(target))
    const { status }: { status: number } = yield call(updateTodo, target)
    if (status !== 200) {
      throw new Error("Failed to update todo")
    }
    yield put({ type: "TODOS_FETCH_REQUESTED" })
  } catch (e: any) {
    yield put(todosPiping.optimalUpdateTodo(og))
    yield put(todosPiping.todosFetchFailed(e.message ?? ("" as string)))
  }
}

function* deleteTodoAction({ payload }: { type: "DELETE_TODO_REQUESTED"; payload: Todo }) {
  yield deleteTodo(payload)
  yield put({ type: "TODOS_FETCH_REQUESTED" })
}

function* clearError({}: { type: "CLEAR_ERROR" }) {
  yield put(todosPiping.todosClearError())
}

function* rootSaga() {
  yield takeEvery("TODOS_FETCH_REQUESTED", getTodosAction)
  yield takeEvery("CREATE_TODO_REQUESTED", createTodoAction)
  yield takeLatest("UPDATE_TODO_REQUESTED", updateTodoAction)
  yield takeLatest("DELETE_TODO_REQUESTED", deleteTodoAction)
  yield takeEvery("CONSUME_ERROR", clearError)
}

export default rootSaga

export const todosSagaActions = {
  getTodosAction,
  createTodoAction,
  updateTodoAction,
  deleteTodoAction,
  clearError,
}

